var toastr = {
    options: {
        closeButton: true,
        debug: false,
        newestOnTop: true,
        progressBar: true,
        positionClass: "toast-top-right",
        preventDuplicates: true,
        onclick: null,
        showDuration: 300,
        hideDuration: 1000,
        timeOut: 5000,
        extendedTimeOut: 1000,
        showEasing: "swing",
        hideEasing: "linear",
        showMethod: "fadeIn",
        hideMethod: "fadeOut"
    },
    success: function (message) {
        console.log("Success: " + message);
        toastr._createToast(message, "toast-success");
    },
    error: function (message) {
        console.log("Error: " + message);
        toastr._createToast(message, "toast-error");
    },
    info: function (message) {
        console.log("Info: " + message);
        toastr._createToast(message, "toast-info");
    },
    warning: function (message) {
        console.log("Warning: " + message);
        toastr._createToast(message, "toast-warning");
    },
    _createToast: function (message, type) {
        debugger;
        var toast = document.createElement("div");
        toast.className = "toast " + type;
        toast.innerText = message;

        var progress = document.createElement("div");
        progress.className = "progress-bar";
        toast.appendChild(progress);

        document.body.appendChild(toast);

        setTimeout(function () {
            toast.style.opacity = "0";
            setTimeout(function () {
                document.body.removeChild(toast);
            }, 500);
        }, toastr.options.timeOut);
    }
};
